import { useRef, useState } from "react"
import { useDispatch } from 'react-redux';
import { setUser } from './formSlice'

export default function Form(){
    const [login, setLogin] = useState('')
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [repeatPass, setRepeatPass] = useState('')
    const [surName, setSurName] = useState('')
    const [age, setAge] = useState('')
    const [sex, setSex] = useState('Male')
    const [city, setCity] = useState('Ufa')
    const [number, setNumber] = useState('')
    const dispatch = useDispatch();
    const inputPass = useRef(null)

    const [inputValues, setInputValues] = useState({
        login: {
            value: '', 
            type: 'text', 
            placeholder: 'Логин',
            name: 'login',
        }, 
        name: {
            value: '', 
            type: 'text', 
            placeholder: 'Имя',
            name: 'name',
        }, 
        email: {
            value: '', 
            type: 'text', 
            placeholder: 'Почта',
            name: 'email',
        }, 
        password: {
            value: '', 
            type: 'repeatPass', 
            placeholder: 'password',
            name: 'password',
        }, 
        repeatPass: {
            value: '', 
            type: 'repeatPass', 
            placeholder: 'repeatPass',
            name: 'repeatPass',
        }, 
        surName: {
            value: '', 
            type: 'surName', 
            placeholder: 'surName',
            name: 'surName',
        }, 
        age: {
            value: '', 
            type: 'age', 
            placeholder: 'age',
            name: 'age',
        }, 
        sex: {
            value: '', 
            type: 'text', 
            placeholder: 'sex',
            name: 'sex',
            options: {
                Male: 'Мужской',
                Female: 'Женский',
            }
        }, 
        city: {
            value: '', 
            type: 'city', 
            placeholder: 'city',
            name: 'city',
            options: {
                Ufa: 'Уфа',
                Another: 'Другой',
            }
        }, 
        number: {
            value: '', 
            type: 'number', 
            placeholder: 'number',
            name: 'number',
        },
      });

    const handleOnChange = event => {

    };

    const setNewUser = () => {
        const obj = {
            login: login,
            name: name,
            email: email,
            password: password,
            surname: surName,
            age: age,
            sex: sex,
            city: city,
            number: number, 
        }

        dispatch(setUser(obj))
    }

    const inputChange = (e) => {
        const { name, value } = e.target;
        setInputValues({ ...inputValues, [name]: value });
    }


    const togglePassVisible = () => {
        if(inputPass.current.type === "password"){
            inputPass.current.type = "text"
        }else{
            inputPass.current.type = "password"
        }
    }

    return(
        <div className="form registration">
            <div className="form__title">Регистрация</div>
            <form >
                <label className="form__label">
                    <div className="form__label__title">* Логин</div>
                    <input type="text" value={login} key="login" onChange={e => setLogin(e.target.value)} placeholder="helloworld"/>
                </label>
                <label className="form__label">
                    <div className="form__label__title">* Имя</div>
                    <input type="text" value={name} key="name" onChange={e => setName(e.target.value)}  placeholder="Евгений Петров"/>
                </label>
                <label className="form__label">
                    <div className="form__label__title">* E-mail</div>
                    <input type="text" value={email} key="email" onChange={e => setEmail(e.target.value)} placeholder="evgeny.petrov@gmail.com"/>
                </label>
                <div className="form__label">
                    <div className="form__label__title">* Пароль</div>
                    <input type="password" ref={inputPass} key="password" value={password} onChange={e => setPassword(e.target.value)} placeholder=""/>
                    <div className="form__switcher hidden" onClick={togglePassVisible}></div>
                </div>
                <div className="form__label">
                    <div className="form__label__title">* Повторите пароль</div>
                    <input type="password" key="repeatPass" value={repeatPass} onChange={e => setRepeatPass(e.target.value)}/>
                </div>
                <div className="form__more">
                    <div className="form__more__container">
                        <label className="form__label">
                            <div className="form__label__title">Фамилия</div>
                            <input type="text" key="surName" placeholder="Петров" value={surName} onChange={e => setSurName(e.target.value)}/>
                        </label>
                        <label className="form__label">
                            <div className="form__label__title">Возраст</div>
                            <input type="text" key="age" value={age} onChange={e => setAge(e.target.value)} placeholder="21 год"/>
                        </label>
                        <label className="form__label">
                            <div className="form__label__title">Пол</div>
                            <select value={sex} key="sex" onChange={e => setSex(e.target.value)}>
                                <option value="Male">Мужской</option>
                                <option value="Female">Женский</option>
                            </select>
                        </label>
                        <label className="form__label">
                            <div className="form__label__title">Город</div>
                            <select value={city} key="city" onChange={e => setCity(e.target.value)}>
                                <option value="Ufa">Уфа</option>
                                <option value="Another">Другие города</option>
                            </select>
                        </label>
                        <label className="form__label">
                            <div className="form__label__title">Телефон</div>
                            <input type="text" key="number" value={number} onChange={e => setNumber(e.target.value)} placeholder="+7(800) 555-55-55"/>
                        </label>
                    </div>
                    <div className="form__more__title"></div>
                </div>
                <input key="submit" type="submit" value="Зарегистрироваться"/>
                <span onClick={setNewUser} className="come_in">Войти</span>
            </form>
        </div>
    )
}
