import { useRef, useState } from "react"
import { useDispatch } from 'react-redux';
import { setUser } from './formSlice'
import Input from '../input/input'

export default function Form(){
    const dispatch = useDispatch();
    const inputPass = useRef(null)

    const [inputValues, setInputValues] = useState([
        {
            value: '', 
            type: 'text', 
            placeholder: 'Логин',
            name: 'login',
        }, 
        {
            value: '', 
            type: 'text', 
            placeholder: 'Имя',
            name: 'name',
        }, 
        {
            value: '', 
            type: 'text', 
            placeholder: 'Почта',
            name: 'email',
        }, 
        {
            value: '', 
            type: 'repeatPass', 
            placeholder: 'password',
            name: 'password',
        }, 
        {
            value: '', 
            type: 'repeatPass', 
            placeholder: 'repeatPass',
            name: 'repeatPass',
        }, 
        {
            value: '', 
            type: 'surName', 
            placeholder: 'surName',
            name: 'surName',
        }, 
        {
            value: '', 
            type: 'age', 
            placeholder: 'age',
            name: 'age',
        }, 
        {
            value: '', 
            type: 'text', 
            placeholder: 'sex',
            name: 'sex',
            options: {
                Male: 'Мужской',
                Female: 'Женский',
            }
        }, 
        {
            value: '', 
            type: 'city', 
            placeholder: 'city',
            name: 'city',
            options: {
                Ufa: 'Уфа',
                Another: 'Другой',
            }
        }, 
        {
            value: '', 
            type: 'number', 
            placeholder: 'number',
            name: 'number',
        },
    ]);

    const setNewUser = () => {

        dispatch(setUser(inputValues))
    }

    const inputChange = (e) => {
        const { key, value } = e.target;
        setInputValues({ ...inputValues, [name]: value });
    }


    const togglePassVisible = () => {
        if(inputPass.current.type === "password"){
            inputPass.current.type = "text"
        }else{
            inputPass.current.type = "password"
        }
    }
    return(
        <div className="form registration">
            <div className="form__title">Регистрация</div>
            <form >
                {Object.keys(inputValues).map(key => {
                    const inputVal = inputValues[key];
                    <Input 
                        type={inputVal.type}
                        placeholder={inputVal.placeholder}
                        changeFunc={inputChange}
                        name={inputVal.name}
                        value={inputVal.value}
                        options={inputVal.options ? inputVal.options : null}
                    />
                })}
                <input key="submit" type="submit" value="Зарегистрироваться"/>
                <span onClick={setNewUser} className="come_in">Войти</span>
            </form>
        </div>
    )
}


