import { useRef } from 'react'
import { useEffect } from 'react'
import './ScrollWrapper.css'

export default function ScrollWrapper(children){
    let scrollWrapperClass = "scroll_wrapper"
    const [wrapper, setWrapper] = useRef(null)

    useEffect(() => {
        const wrapperHeight = wrapper;
        console.log(wrapperHeight)
    })
    return(
        <div className={scrollWrapperClass} ref={wrapper}>
            <div class="scroll_wrapper__scroll"></div>
            <div class="scroll_wrapper__content">
                {children}
            </div>
        </div>
    )
}
