export default function Input(props){
    const { type, placeholder, changeFunc, name, value, options } = props
    return (
        <label className="form__label">
             <div className="form__label__title">{name}</div>
        {options ? 
            <input type={type} placeholder={placeholder} value={value} onChange={e => changeFunc(e)}/>
            :
            <select value={value} key="sex" onChange={e => changeFunc(e)}>
                <option value="Male">Мужской</option>
                <option value="Female">Женский</option>
            </select>
        }
        </label>
    )
}
