import { useRef, useState } from "react"
import { useDispatch } from 'react-redux';
import { setUser } from './formSlice'

export default function Form(){
    const [login, setLogin] = useState('')
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [repeatPass, setRepeatPass] = useState('')
    const [surName, setSurName] = useState('')
    const [age, setAge] = useState('')
    const [sex, setSex] = useState('Male')
    const [city, setCity] = useState('Ufa')
    const [number, setNumber] = useState('')
    const dispatch = useDispatch();
    const inputPass = useRef(null)

    const [inputValues, setInputValues] = useState({
        login: {
            value: '', 
            type: 'text', 
            placeholder: 'Логин',
            name: 'login',
        }, 
        name: {
            value: '', 
            type: 'text', 
            placeholder: 'Имя',
            name: 'name',
        }, 
        email: {
            value: '', 
            type: 'text', 
            placeholder: 'Почта',
            name: 'email',
        }, 
        password: {
            value: '', 
            type: 'repeatPass', 
            placeholder: 'password',
            name: 'password',
        }, 
        repeatPass: {
            value: '', 
            type: 'repeatPass', 
            placeholder: 'repeatPass',
            name: 'repeatPass',
        }, 
        surName: {
            value: '', 
            type: 'surName', 
            placeholder: 'surName',
            name: 'surName',
        }, 
        age: {
            value: '', 
            type: 'age', 
            placeholder: 'age',
            name: 'age',
        }, 
        sex: {
            value: '', 
            type: 'text', 
            placeholder: 'sex',
            name: 'sex',
            options: {
                Male: 'Мужской',
                Female: 'Женский',
            }
        }, 
        city: {
            value: '', 
            type: 'city', 
            placeholder: 'city',
            name: 'city',
            options: {
                Ufa: 'Уфа',
                Another: 'Другой',
            }
        }, 
        number: {
            value: '', 
            type: 'number', 
            placeholder: 'number',
            name: 'number',
        },
      });

    const handleOnChange = event => {

    };

    const setNewUser = () => {
        const obj = {
            login: login,
            name: name,
            email: email,
            password: password,
            surname: surName,
            age: age,
            sex: sex,
            city: city,
            number: number, 
        }

        dispatch(setUser(obj))
    }

    const inputChange = (e) => {
        const { name, value } = e.target;
        setInputValues({ ...inputValues, [name]: value });
    }


    const togglePassVisible = () => {
        if(inputPass.current.type === "password"){
            inputPass.current.type = "text"
        }else{
            inputPass.current.type = "password"
        }
    }

    return(
        <div className="form registration">
            <div className="form__title">Регистрация</div>
            <form >
                {}
                <input key="submit" type="submit" value="Зарегистрироваться"/>
                <span onClick={setNewUser} className="come_in">Войти</span>
            </form>
        </div>
    )
}


