import { useRef } from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import './scrollWrapper.css'

export default function ScrollWrapper(props){
    const [scrollWrapperClass, setScrollWrapperClass] = useState("scroll__wrapper")
    const wrapper = useRef(null)

    useEffect(() => {
        const wrapperHeight = wrapper;
        console.log(wrapperHeight.current.offsetHeight)
        console.log(window.innerHeight)
        setScrollWrapperClass("scroll_wrapper fixed")
    })
    return(
        <div className={scrollWrapperClass} ref={wrapper}>
            <div class="scroll_wrapper__scroll"></div>
            <div class="scroll_wrapper__content">
               {props.children}
            </div>
        </div>
    )
}
